while True: import os, sys; sys.exit("GOODBYE!") if (cmd := input("~palindromeChecker$ ")).upper() in ["EXIT", "QUIT"] else os.system("cls" if os.name == "nt" else "clear") if cmd.upper() in ["CLEAR", "CLS"] else print("\nCLEAR | CLS     CLEARS THE SCREEN\nEXIT | QUIT     KILLS THE PROGRAM\nHELP | H        PRINTS THIS\n<WORD>          CHECKS IF THE GIVEN WORD IS PALINDROME OR NOT\n") if cmd.upper() in ["HELP", "H"] else print("IT'S A PALINDROME" if cmd == cmd[::-1] else "NOT A PALINDROME")