while True: import os, sys, re; cmd = input("~palindromeChecker$ ").strip().upper(); sys.exit("GOODBYE!") if cmd in ["EXIT", "QUIT"] else os.system("cls" if os.name == "nt" else "clear") if cmd in ["CLEAR", "CLS"] else print("\nHELP | H        PRINTS THIS\nEXIT | QUIT     KILLS THE PROGRAM\nCLEAR | CLS     CLEARS THE SCREEN\n<WORD>          CHECKS IF THE GIVEN WORD IS A PALINDROME OR NOT") if cmd in ["HELP", "H"] else print("IT'S A PALINDROME" if (word := re.sub(r'[^A-Za-z0-9]', '', cmd)) == word[::-1] else "NOT A PALINDROME")